		var i1 = this._lon2LevelZeroIndex(bbox[0]);
		var j1 = this._lat2LevelZeroIndex(bbox[3]);
		var i2 = this._lon2LevelZeroIndex(bbox[2]);
		var j2 = this._lat2LevelZeroIndex(bbox[1]);
		
		
