			var height = elevations ? elevations[ offset ] : 0.0;
			this.config.coordinateSystem.fromGeoTo3D( [lon, lat, height], pos3d );
			var x = pos3d[0];
			var y = pos3d[1];
			var z = pos3d[2];

			var vi = offset * vertexSize;
			vertices[vi] = invMatrix[0]*x + invMatrix[4]*y + invMatrix[8]*z + invMatrix[12];
